mapper.update:

update student
set `name` = #{name,jdbcType=VARCHAR},
  `password` = #{password,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where sid = #{sid,jdbcType=INTEGER}

mapper.select:

select sid, `name`, `password`, create_time, update_time
from student
where sid = #{sid,jdbcType=INTEGER}
